      </div>
      <template #footer>
        <div class="drawer-footer">
          <Button @click="onSettings" label="Settings" icon="pi pi-cog" outlined></Button>
          <Button @click="onLogout" label="Logout" icon="pi pi-sign-out" severity="danger" outlined></Button>
        </div>
      </template>
    </Drawer>
  </div>
</template>

